    yield put({ type: 'ACCOUNT_BALANCE_FAILED', error })
    console.error('Error fetching account ' + account + ' balance:')
    console.error(error)
  }

  yield put({ type: 'ACCOUNT_BALANCES_FETCHED' })
}

